$(function(){document.querySelectorAll('a[href^="#"]').forEach(e=>{e.addEventListener("click",function(e){e.preventDefault(),document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})})}),$(".nav-link").click(function(){$(".nav-link").removeClass("active"),$(this).addClass("active")}),$(window).scroll(function(){$(window).scrollTop()>=100?$(".navbar").addClass("nav-shadow"):$(".navbar").removeClass("nav-shadow")})});